INSERT INTO squad VALUES (1, 'U12', 'The Inspire Girls Academy Under 12''s team provides a platform for young girls passionate about football, aspiring to become professional athletes. Our committed passion to driving success has allowed former, and current players the opportunity in securing full-time contracts at the Brighton FC football academy as well as some of our current players having been selected for the Oxford Town and Reading Emerging Talent Centres which have opened avenues for further opportunities in their football journeys.', 'u12-team.jpg', 'U12');
